import Foundation
import UIKit

struct DropboxIcon {
    // Doing base64 encoding because of Swift Package Manger's difficulties with assets. Revisit this after Swift 5.3
    let base64 = ""
    let image:UIImage
    
    init?() {
        guard let decodedData = Data(base64Encoded: base64) else {
            return nil
        }

        guard let image = UIImage(data: decodedData) else {
            return nil
        }
        
        self.image = image
    }
}
